html, body {height:100%;}

.wrap {
/*центрируем*/
  position:relative;
  width:100%; /*могут быть и пиксели*/
  margin: 0px auto ;
/*вытягиваем в высоту*/
  height:auto !important;
  height:100%;
  min-height:100%;
}

.container-fluid {
  padding-left: 0px;
  padding-right: 0px;
}

#head-navbar {
  width: 100%;
  white-space: nowrap;
  overflow: hidden;
  height: 40px;
  padding: 0 5px;
}

.nav>li {
  display: inline-block;
  width: initial;
  float: left;
  margin-right: 10px;
  white-space: nowrap;
}

.nav {
  display: inline-block;
}

.nav-link {
  padding: 0px;
}

@media screen and (max-width:768px)
{
  .nav {
    display: inline-block;
  }
}

.navbar-dark .navbar-nav .active .nav-link {
  color: hsl(348, 100%, 71%);
}

.glow-button {
  box-shadow: 0 4px 15px rgba(255, 0, 0, .4) !important;
  outline: 4px auto  rgba(255, 0, 0, .4) !important;
}


